document.addEventListener("click",(e=>{e.target.matches("button")&&e.target.focus()}));const heroBtn=document.querySelector(".hero__btn"),overlay=document.querySelector(".overlay"),modal=document.querySelector(".modal");heroBtn.addEventListener("click",(()=>{overlay.classList.add("overlay_active"),modal.classList.add("modal_active")})),overlay.addEventListener("click",(e=>{const t=e.target;(t===overlay||t.closest(".modal__close"))&&(overlay.classList.remove("overlay_active"),modal.classList.remove("modal_active"))})),modal.click((function(e){modalContent.is(e.target)||0!==modalContent.has(e.target).length||($(this).removeClass("modal_active"),modalOverlay.removeClass("overlay_active"))}));const modalTitle=$(".modal__title"),modalForm=$(".modal__form");modalForm.submit((function(e){e.preventDefault(),$.ajax({url:"https://jsonplaceholder.typicode.com/posts",type:"POST",data:$(this).serialize(),success(e){modalTitle.text("Спасибо, ожидайте звонок"),modalForm.slideUp(300)},error(){modalTitle.text("Косячок")}})}));